                        , EMP_PWD
                        , EMP_PHN
                        , EMP_EML
                        , EMP_ST_CD
                        , EMP_RNK_CD
                        , EMP_HR_DT
                        , EMP_BRT_DT
                        , REG_DTM)
VALUES ( #{empNm}
       , #{empPwd}
       , #{empPhn}
       , #{empEml}
       , #{empStCd}
       , #{empRnkCd}
       , #{empHrDt}
       , #{empBrtDt}
       , sysdate())

mapper.select:
select EMP_EML empEml
FROM TB_EMPLOYEE
WHERE EMP_EML = #{empEml}

