mapper.insert:
INSERT INTO TB_VACATION ( EMP_NO, VCTN_STR_DT, VCTN_END_DT, VCTN_KND_CD, VCTN_RSN, VCTN_RSD_CNT, VCTN_APL_DTM, VCTN_DAY_CNT ) VALUES ( #{empNo}, #{vctnStrDt}, #{vctnEndDt}, #{vctnKndCd}, #{vctnRsn}, ( SELECT a.EMP_VCTN_TTL - IFNULL(b.cnt, 0) FROM TB_EMPLOYEE a LEFT OUTER JOIN ( SELECT IFNULL(SUM(z.VCTN_DAY_CNT), 0) cnt, z.EMP_NO FROM TB_VACATION z WHERE VCTN_ST_CD = '02' GROUP BY z.EMP_NO ) b ON a.EMP_NO = b.EMP_NO WHERE a.EMP_NO = #{empNo} ), NOW(),
#{vctnHalfCnt}
#{vctnDayCnt}
)

mapper.update:
UPDATE TB_VACATION
SET VCTN_ST_CD = #{vctnStCd}
WHERE VCTN_NO = #{vctnNo}
